import React, {useState} from "react"
import { minutesToDuration } from "../utils/duration";

function BreakDuration({session,breakDuration, setBreakDuration}) {
    const handleIncrease = () => setBreakDuration((breakDuration) => (parseInt(breakDuration) + 1))
    const handleDecrease= () => setBreakDuration((breakDuration) => (parseInt(breakDuration) - 1))

    const disableDecrease = (() => session || parseInt(breakDuration) === 1  ?true :false )()
    const disableIncrease = (() => session || parseInt(breakDuration) === 15 ?true :false )()

    const durationInMinutes = minutesToDuration()
    return <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}

                Break Duration: {breakDuration}:00
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button" onClick ={handleDecrease}
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  id = "decrease"
                  disabled = {disableDecrease}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button" onClick ={handleIncrease}
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  id = "increase"
                  disabled = {disableIncrease}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
}

export default BreakDuration
