import React, {useState} from "react"
import { minutesToDuration } from "../utils/duration";

function FocusDuration ( {session, focusDuration, setFocusDuration}) {
const handleIncrease = () => setFocusDuration(focusDuration + 1)
const handleDecrease = () => setFocusDuration(focusDuration - 1)

const disableDecrease = (() => session || focusDuration === 5  ? true :false )()
const disableIncrease = (() => session || focusDuration === 60 ?true :false )()

const durationInMinute = minutesToDuration(focusDuration)

return <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {durationInMinute}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button onClick ={handleDecrease} 
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                disabled = {disableDecrease}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button onClick ={handleIncrease}
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                disabled = {disableIncrease}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
        </div>

}

export default FocusDuration
